    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Add mobile-view class to body if mobile detail content exists
            if (document.querySelector('.mobile-detail-content')) {
                document.body.classList.add('mobile-view');
            }

            const images = @json($post->images->pluck('image_path'));
            let currentImageIndex = 0;
            const previewModal = new bootstrap.Modal(document.getElementById('imagePreviewModal'));
            const previewImage = document.getElementById('previewImage');
            const imageCounter = document.querySelector('.image-counter');
            const modalElement = document.getElementById('imagePreviewModal');

            // Function to open image preview
            window.openPreview = function(index) {
                currentImageIndex = index;
                updatePreviewImage();
                previewModal.show();
            };

            // Function to show previous image
            window.prevImage = function() {
                currentImageIndex = (currentImageIndex - 1 + images.length) % images.length;
                updatePreviewImage();
            };

            // Function to show next image
            window.nextImage = function() {
                currentImageIndex = (currentImageIndex + 1) % images.length;
                updatePreviewImage();
            };

            // Update preview image and counter
            function updatePreviewImage() {
                if (previewImage && images[currentImageIndex]) {
                    previewImage.src = "{{ asset('') }}" + images[currentImageIndex];
                    if (imageCounter) {
                        imageCounter.textContent = `${currentImageIndex + 1}/${images.length}`;
                    }
                }
            }

            // Close modal when clicking outside
            if (modalElement) {
                modalElement.addEventListener('click', function(e) {
                    if (e.target === modalElement) {
                        previewModal.hide();
                    }
                });
            }

            // Close modal when clicking close button
            const closeButton = document.querySelector('.btn-close');
            if (closeButton) {
                closeButton.addEventListener('click', function() {
                    previewModal.hide();
                });
            }

            // Keyboard navigation
            document.addEventListener('keydown', function(e) {
                if (modalElement && modalElement.classList.contains('show')) {
                    if (e.key === 'ArrowLeft') {
                        prevImage();
                    } else if (e.key === 'ArrowRight') {
                        nextImage();
                    } else if (e.key === 'Escape') {
                        previewModal.hide();
                    }
                }
            });

            // Những hàm handleSave và toggleSave đã được chuyển sang file trustlist-handler.js
        });

        function sharePost() {
            const modal = document.getElementById('shareModal');
            const shareUrl = document.getElementById('shareUrl');
            shareUrl.value = window.location.href;
            modal.classList.add('show');
        }

        function closeShareModal() {
            const modal = document.getElementById('shareModal');
            modal.classList.remove('show');
        }

        function copyShareUrl() {
            const shareUrl = document.getElementById('shareUrl');
            const copyBtn = document.querySelector('.share-copy-btn');

            shareUrl.select();
            document.execCommand('copy');

            copyBtn.textContent = 'Đã sao chép!';
            copyBtn.classList.add('copied');

            setTimeout(() => {
                copyBtn.textContent = 'Sao chép';
                copyBtn.classList.remove('copied');
            }, 2000);
        }

