        function removeOwner(e) {
            e.preventDefault();
            const selectedOwner = document.getElementById('selectedOwner');
            selectedOwner.innerHTML = '<div class="text-muted">Chưa có chủ sở hữu được chọn</div>';
            document.getElementById('ownerIdInput').value = '';
